@SpringBootApplication
@ComponentScan("com.your.package") // Explicitly declare your base package
public class Application { ... }

@Autowired
@Qualifier("specificBeanName") // Add qualifier for ambiguous dependencies
private MyService service;

@Autowired
@Lazy
private DependentService service;

// 良好实践示例
@RestController
@RequestMapping("/api/users")
@CrossOrigin
public class UserController {

    @Autowired
    private UserService userService;

    // 明确的参数绑定
    @GetMapping("/{id}")
    public ResponseEntity<User> getUser(@PathVariable Long id) {
        return ResponseEntity.ok(userService.getById(id));
    }

    // 规范的异常处理
    @ExceptionHandler(ResourceNotFoundException.class)
    public ResponseEntity<?> handleNotFound() {
        return ResponseEntity.notFound().build();
    }
}

entry.value:
package com.sulei.service.imp;

import com.sulei.mapper.StudentMapper;
import com.sulei.pojo.Student;
import com.sulei.service.StudentService;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.stereotype.Service;

import java.util.List;

/**
 * 学生服务实现类
 * 实现学生相关的业务逻辑
 */
@Service
public class StudentServiceImpl implements StudentService {

    @Autowired
    private StudentMapper studentMapper;

    /**
     * 获取所有学生信息
     * @return 学生列表
     */
    @Override
    public List<Student> getAllStudents() {
        return studentMapper.getAllStudents();
    }

    /**
     * 新增学生信息
     * @param student 学生对象
     */
    @Override
    public void addStudent(Student student) {
        studentMapper.addStudent(student);
    }

    @Override
    public void updateStudent(Student student) {
        studentMapper.updateStudent(student);
    }

    @Override
    public void deleteStudent(Integer id) {
        studentMapper.deleteStudent(id);
    }
}

